import React, { useState, useEffect, useCallback } from "react";
import Card from "./Card";
import "./App.css";

const generateDeck = () => {
  const contents = ["A", "B", "C", "D", "E", "F", "G", "H"];
  return [...contents, ...contents]
    .sort(() => Math.random() - 0.5)
    .map((content, index) => ({ id: index, content, isMatched: false }));
};

const App = () => {
  const [deck, setDeck] = useState(generateDeck);
  const [flippedCards, setFlippedCards] = useState([]);
  const [matchedPairs, setMatchedPairs] = useState(0);
  const [attempts, setAttempts] = useState(0);

  useEffect(() => {
    if (flippedCards.length === 2) {
      setAttempts((prev) => prev + 1);
      const [firstCard, secondCard] = flippedCards;
      if (firstCard.content === secondCard.content) {
        setDeck((prevDeck) =>
          prevDeck.map((card) =>
            card.content === firstCard.content
              ? { ...card, isMatched: true }
              : card
          )
        );
        setMatchedPairs((prev) => prev + 1);
      }
      setTimeout(() => setFlippedCards([]), 1000);
    }
  }, [flippedCards]);

  const handleCardClick = useCallback(
    (card) => {
      if (flippedCards.length < 2 && !flippedCards.includes(card)) {
        setFlippedCards((prev) => [...prev, card]);
      }
    },
    [flippedCards]
  );

  const resetGame = () => {
    setDeck(generateDeck());
    setFlippedCards([]);
    setMatchedPairs(0);
    setAttempts(0);
  };

  return (
    <div className="App">
      <h1>Nextdoor: Memory Game</h1>
      <div className="score">Attempts: {attempts}</div>
      <div className="board">
        {deck.map((card) => (
          <Card
            key={card.id}
            card={card}
            onClick={handleCardClick}
            isFlipped={flippedCards.includes(card) || card.isMatched}
          />
        ))}
      </div>
      {matchedPairs === deck.length / 2 && (
        <div className="win-message">You Win!</div>
      )}
      <button className="reset-button" onClick={resetGame}>
        Reset Game
      </button>
    </div>
  );
};

export default App;
